      font-size: 12px !important; /* text-xs */
      margin-top: 2px !important; /* mt-0.5 */
      font-weight: 500 !important;
      background: #ffffff !important;
    }

    .pfd-info-text {
      line-height: 1.4 !important;
    }

    .pfd-custom-option-content {
      margin-top: 12px !important;
    }

    .pfd-custom-option-content p {
      margin: 0 !important;
      font-size: 14px !important;
      line-height: 1.4 !important;
    }

    .pfd-split-option .t4s-swatch__title--right {
      position: absolute !important;
      right: 0 !important;
      top: 0 !important;
      font-size: 14px !important;
    }

    .pfd-split-option .t4s-swatch__title--right .bold {
      font-weight: 600 !important;
      color: var(--pfd-text-color) !important;
      font-family: var(--pfd-font-family-weighted) !important;
    }

    .t4s-swatch__item.swatch-loading,
    .pfd-standard-options .t4s-swatch__item.swatch-loading {
      opacity: 0.6 !important;
      pointer-events: none !important;
    }

    .t4s-swatch__item.swatch-error,
    .pfd-standard-options .t4s-swatch__item.swatch-error {
      background: #f5f5f5 !important;
      color: var(--pfd-muted-color) !important;
      border-color: #ddd !important;
    }

    .t4s-swatch__item:focus,
    .pfd-standard-options .t4s-swatch__item:focus {
      outline: 2px solid var(--pfd-border-color-selected) !important;
      outline-offset: 2px !important;
    }

    @media (max-width: 1536px) {
      .t4s-swatch__btn-wrap {
        width: calc(33% - 6px) !important;
      }
    }

    @media (max-width: 1080px) {
      .t4s-swatch__btn-wrap {
        width: calc(50% - 4px) !important;
      }
    }

    @media (max-width: 768px) {
      .t4s-swatch__btn-wrap {
        width: calc(33% - 6px) !important;
      }

      .pfd-split-selection {
        flex-direction: column !important;
        align-items: flex-start !important;
        gap: 16px !important;
      }

      .pfd-standard-options {
        width: 100% !important;
      }

      .pfd-or-separator {
        display: none;
      }

      .pfd-custom-dropdown {
        width: 100% !important;
        min-width: 200px !important;
      }

      .pfd-or-separator {
        align-self: center !important;
        margin: 0 !important;
      }
    }

    @media (max-width: 480px) {
      .t4s-swatch__btn-wrap {
        width: calc(50% - 4px) !important;
      }

      .t4s-swatch__item,
      .pfd-standard-options .t4s-swatch__item {
        min-width: 45px !important;
        height: 36px !important;
        font-size: 13px !important;
        padding: 0 8px !important;
      }

      .pfd-split-option .t4s-swatch__title--right {
        position: static !important;
        display: block !important;
      }

      .pfd-custom-dropdown {
        height: 36px !important;
        font-size: 13px !important;
      }
    }
  }
}

/* ============================================
   AWAITING RESTOCK BADGE
   Glassmorphism badge positioned over swatch image
   ============================================ */

/* Ensure wrappers are positioned for badge anchoring */
.t4s-swatch__btn-wrap,
.t4s-swatch__item {
  position: relative;
}

div[data-img-el] {
  position: relative !important;
}

/* Badge base styles */
.t4s-swatch__restock-badge {
  position: absolute;
  bottom: 8px;
  left: 50%;
  transform: translateX(-50%);

  background: rgba(0, 0, 0, 0.05);
  backdrop-filter: blur(10px) saturate(1.2);
  -webkit-backdrop-filter: blur(10px) saturate(1.2);

  color: rgba(0, 0, 0, 0.85);

  font-size: 8px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  padding: 5px 10px;
  border-radius: 4px;
  white-space: nowrap;
  z-index: 2;
  pointer-events: none;

  border: 1px solid rgba(0, 0, 0, 0.08);

  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
}

/* ============================================
   RESTOCK BADGE VISIBILITY CONTROL
   Class-based toggle for JS compatibility
   ============================================ */

/* Hide badge when it's a direct child of the wrapper (not inside image container) */
.t4s-swatch__btn-wrap > .t4s-swatch__restock-badge {
  display: none !important;
}

/* Default: hide badge inside image container */
.t4s-swatch__btn-wrap [data-img-el] .t4s-swatch__restock-badge {
  display: none !important;
}

/* Show only when JS adds .is-visible class */
.t4s-swatch__btn-wrap [data-img-el] .t4s-swatch__restock-badge.is-visible {
  display: flex !important;
  align-items: center;
  justify-content: center;
}

/* For non-image swatches (length/text options): badge on the item itself */
.t4s-swatch__item > .t4s-swatch__restock-badge {
  display: none !important;
}

.t4s-swatch__item > .t4s-swatch__restock-badge.is-visible {
  display: block !important;
}

/* Content-aware contrast: Light background (dark hair) = dark text */
[data-luminance="light"] .t4s-swatch__restock-badge {
  background: rgba(0, 0, 0, 0.05);
  color: rgba(0, 0, 0, 0.85);
  border-color: rgba(0, 0, 0, 0.1);
}

/* Content-aware contrast: Dark background (light hair) = light text */
[data-luminance="dark"] .t4s-swatch__restock-badge {
  background: rgba(255, 255, 255, 0.05);
  color: rgba(255, 255, 255, 0.95);
  border-color: rgba(255, 255, 255, 0.15);
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
}

/* Subtle hover effect on out-of-stock swatches */
.t4s-swatch__btn-wrap.is--out-of-stock:hover .t4s-swatch__restock-badge,
.t4s-swatch__item.is--out-of-stock:hover .t4s-swatch__restock-badge {
  backdrop-filter: blur(12px) saturate(1.3);
  -webkit-backdrop-filter: blur(12px) saturate(1.3);
  opacity: 0.9;
}

/* Keep full opacity on out-of-stock swatches for badge legibility */
.t4s-swatch__btn-wrap.is--out-of-stock .t4s-swatch__item,
.t4s-swatch__item.is--out-of-stock,
.t4s-swatch__btn-wrap.is--out-of-stock div[data-img-el],
.t4s-swatch__btn-wrap.is--out-of-stock div[data-img-el] img {
  opacity: 1 !important;
}

/* ============================================
   AVAILABLE LENGTHS TEXT
   Shown below the swatch image, as a sibling in .t4s-swatch__btn-wrap
   Displayed when a color is out of stock at the selected length
   but available in other lengths
   ============================================ */

/* Base: hidden by default */
.t4s-swatch__available-lengths {
  display: none;
  width: 100%;
  text-align: center;
  margin-top: 4px;
  /* Fallback before JS sets data-luminance: dark text with a white halo so it
     reads on both light and dark swatch backgrounds without JS. */
  color: rgba(20, 20, 20, 0.9);
  text-shadow:
    0 0 3px rgba(255, 255, 255, 0.9),
    0 0 6px rgba(255, 255, 255, 0.7);
  font-size: 7px;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.3px;
  white-space: normal;
  word-break: break-word;
  pointer-events: none;
  line-height: 1.3;
  max-width: 100%;
}

/* Show when Liquid SSR or JS adds .is-visible */
.t4s-swatch__available-lengths.is-visible {
  display: block;
}

/* Direct child of btn-wrap: hidden by default, shown via .is-visible */
.t4s-swatch__btn-wrap > .t4s-swatch__available-lengths {
  display: none;
}

.t4s-swatch__btn-wrap > .t4s-swatch__available-lengths.is-visible {
  display: block;
}

/* Content-aware contrast: attribute set on btn-wrap by JS luminance detection.
   Once luminance is known, replace the halo text-shadow with a clean color. */
.t4s-swatch__btn-wrap[data-luminance="light"] .t4s-swatch__available-lengths {
  color: rgba(20, 20, 20, 0.75);
  text-shadow: none;
}

.t4s-swatch__btn-wrap[data-luminance="dark"] .t4s-swatch__available-lengths {
  color: rgba(255, 255, 255, 0.9);
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.4);
}

/* Reflow btn-wrap to column layout when lengths text is visible.
   Scoped inside .pfd-override-wrapper to beat the nested align-items: center
   !important on .t4s-swatch__btn-wrap from the base rule above.
   .pfd-has-lengths is added by Liquid SSR and kept in sync by JS. */
.pfd-override-wrapper .t4s-swatch__btn-wrap.pfd-has-lengths,
.pfd-override-wrapper .t4s-swatch__btn-wrap:has(> .t4s-swatch__available-lengths.is-visible) {
  display: flex !important;
  flex-direction: column !important;
  align-items: flex-start !important;
  gap: 2px !important;
}

/* In column layout, reset badge position */
.pfd-override-wrapper .t4s-swatch__btn-wrap.pfd-has-lengths > .t4s-swatch__restock-badge,
.pfd-override-wrapper .t4s-swatch__btn-wrap:has(> .t4s-swatch__available-lengths.is-visible) > .t4s-swatch__restock-badge {
  margin-left: 0 !important;
  margin-top: 2px;
}

/* Tight spacing below the badge */
.pfd-override-wrapper .t4s-swatch__btn-wrap.pfd-has-lengths > .t4s-swatch__available-lengths,
.pfd-override-wrapper .t4s-swatch__btn-wrap:has(> .t4s-swatch__available-lengths.is-visible) > .t4s-swatch__available-lengths {
  margin-top: 0;
  text-align: center;
  width: 100%;
}

